import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import MovieDetail from './pages/MovieDetail';
import UserAccount from './pages/UserAccount';
import SearchResults from './pages/SearchResults';
import Watchlist from './pages/Watchlist';
import './styles/App.css';

function App() {
  const [user, setUser] = useState(null);
  const [favorites, setFavorites] = useState([]);
  const [watchlist, setWatchlist] = useState([]);
  const [reviews, setReviews] = useState([]);

  // Load user data from localStorage on app start
  useEffect(() => {
    const savedUser = localStorage.getItem('movieUser');
    const savedFavorites = localStorage.getItem('movieFavorites');
    const savedWatchlist = localStorage.getItem('movieWatchlist');
    const savedReviews = localStorage.getItem('movieReviews');

    if (savedUser) setUser(JSON.parse(savedUser));
    if (savedFavorites) setFavorites(JSON.parse(savedFavorites));
    if (savedWatchlist) setWatchlist(JSON.parse(savedWatchlist));
    if (savedReviews) setReviews(JSON.parse(savedReviews));
  }, []);

  // Save to localStorage whenever state changes
  useEffect(() => {
    if (user) localStorage.setItem('movieUser', JSON.stringify(user));
    localStorage.setItem('movieFavorites', JSON.stringify(favorites));
    localStorage.setItem('movieWatchlist', JSON.stringify(watchlist));
    localStorage.setItem('movieReviews', JSON.stringify(reviews));
  }, [user, favorites, watchlist, reviews]);

  const [movies] = useState([
    // Sci-Fi Movies
    {
      id: 1,
      title: "Inception",
      genre: "Sci-Fi",
      rating: 8.8,
      year: 2010,
      director: "Christopher Nolan",
      duration: "148 min",
      description: "A thief who steals corporate secrets through dream-sharing technology is given the inverse task of planting an idea into the mind of a C.E.O.",
      image: "https://images.unsplash.com/photo-1489599809505-7c8e1c75fd6c?w=300&h=450&fit=crop",
      cast: ["Leonardo DiCaprio", "Marion Cotillard", "Tom Hardy", "Joseph Gordon-Levitt"],
      trailer: "https://www.youtube.com/embed/YoHD9XEInc0"
    },
    {
      id: 2,
      title: "The Matrix",
      genre: "Sci-Fi",
      rating: 8.7,
      year: 1999,
      director: "The Wachowskis",
      duration: "136 min",
      description: "A computer hacker learns from mysterious rebels about the true nature of his reality and his role in the war against its controllers.",
      image: "https://images.unsplash.com/photo-1534447677768-be436bb09401?w=300&h=450&fit=crop",
      cast: ["Keanu Reeves", "Laurence Fishburne", "Carrie-Anne Moss", "Hugo Weaving"],
      trailer: "https://www.youtube.com/embed/vKQi3bBA1y8"
    },
    // ... include all your existing movies here, add trailer URLs
    // Add trailer property to all movies like above
  ]);

  const loginUser = (userData) => {
    const userWithId = { ...userData, id: Date.now() };
    setUser(userWithId);
  };

  const logoutUser = () => {
    setUser(null);
    setFavorites([]);
    setWatchlist([]);
    setReviews([]);
  };

  const toggleFavorite = (movieId) => {
    if (!user) {
      alert('Please login to add favorites');
      return;
    }
    
    setFavorites(prev => 
      prev.includes(movieId) 
        ? prev.filter(id => id !== movieId)
        : [...prev, movieId]
    );
  };

  const toggleWatchlist = (movieId) => {
    if (!user) {
      alert('Please login to manage watchlist');
      return;
    }
    
    setWatchlist(prev => 
      prev.includes(movieId) 
        ? prev.filter(id => id !== movieId)
        : [...prev, movieId]
    );
  };

  const addReview = (movieId, rating, comment) => {
    if (!user) {
      alert('Please login to add reviews');
      return;
    }

    const newReview = {
      id: Date.now(),
      movieId,
      userId: user.id,
      userName: user.name,
      rating,
      comment,
      date: new Date().toISOString()
    };

    setReviews(prev => [...prev, newReview]);
  };

  // Get recommended movies based on user's favorites
  const getRecommendedMovies = () => {
    if (favorites.length === 0) return movies.slice(0, 4); // Return popular movies if no favorites

    const favoriteGenres = movies
      .filter(movie => favorites.includes(movie.id))
      .map(movie => movie.genre);

    const genreCount = {};
    favoriteGenres.forEach(genre => {
      genreCount[genre] = (genreCount[genre] || 0) + 1;
    });

    const topGenre = Object.keys(genreCount).reduce((a, b) => 
      genreCount[a] > genreCount[b] ? a : b
    );

    return movies
      .filter(movie => 
        movie.genre === topGenre && !favorites.includes(movie.id)
      )
      .slice(0, 4);
  };

  return (
    <Router>
      <div className="App">
        <Navbar user={user} onLogout={logoutUser} />
        <Routes>
          <Route path="/" element={
            <Home 
              movies={movies} 
              favorites={favorites}
              watchlist={watchlist}
              onToggleFavorite={toggleFavorite}
              onToggleWatchlist={toggleWatchlist}
              recommendedMovies={getRecommendedMovies()}
            />
          } />
          <Route path="/login" element={<Login onLogin={loginUser} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/movie/:id" element={
            <MovieDetail 
              movies={movies} 
              favorites={favorites}
              watchlist={watchlist}
              reviews={reviews}
              onToggleFavorite={toggleFavorite}
              onToggleWatchlist={toggleWatchlist}
              onAddReview={addReview}
              user={user}
            />
          } />
          <Route path="/account" element={
            <UserAccount 
              user={user} 
              favorites={favorites}
              watchlist={watchlist}
              reviews={reviews}
              movies={movies}
            />
          } />
          <Route path="/search" element={
            <SearchResults 
              movies={movies}
              favorites={favorites}
              watchlist={watchlist}
              onToggleFavorite={toggleFavorite}
              onToggleWatchlist={toggleWatchlist}
            />
          } />
          <Route path="/watchlist" element={
            <Watchlist 
              movies={movies.filter(movie => watchlist.includes(movie.id))}
              favorites={favorites}
              onToggleFavorite={toggleFavorite}
              onToggleWatchlist={toggleWatchlist}
            />
          } />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
