// src/pages/UserAccount.js
import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/UserAccount.css';

const UserAccount = ({ user }) => {
  if (!user) {
    return (
      <div className="not-logged-in">
        <h2>Please log in to view your account</h2>
        <Link to="/login" className="auth-redirect">Login</Link>
      </div>
    );
  }

  return (
    <div className="user-account">
      <div className="account-header">
        <h1>Your Account</h1>
      </div>
      <div className="account-info">
        <div className="info-card">
          <h3>Personal Information</h3>
          <div className="info-item">
            <label>Name:</label>
            <span>{user.name}</span>
          </div>
          <div className="info-item">
            <label>Email:</label>
            <span>{user.email}</span>
          </div>
        </div>
        
        <div className="info-card">
          <h3>Preferences</h3>
          <div className="preferences">
            <p>Your favorite genres will appear here based on your watch history.</p>
          </div>
        </div>
        
        <div className="info-card">
          <h3>Watch History</h3>
          <div className="watch-history">
            <p>Movies you've watched will appear here.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserAccount;
